import { type Breakpoint } from '@divi/types';
export type StringArray = string[];
export type StringObject = {
    [key: string]: string;
};
export type ConversionItemObject = {
    path: string;
    type?: 'font' | 'icon' | 'svgTransform' | 'dividersFlip' | 'scroll' | 'borderRadius' | 'spacing' | 'gradient' | 'transform' | 'visibility';
};
export type ConversionItem = string | ConversionItemObject;
export type ConversionObject = {
    [key: string]: ConversionItem;
};
export type NumberObject = {
    [key: string]: number;
};
export type ShortcodeObject = Array<{
    attrs: StringObject;
    content: string | ShortcodeObject;
    name: string;
}>;
export type StickyStatus = {
    active: boolean;
    viewport: Breakpoint;
};
export type FontAttrs = {
    family?: string;
    weight?: string;
    style?: string[];
    lineColor?: string;
    lineStyle?: string;
};
