import { type Sidebar } from '../sidebar';


export namespace Element {
  export interface Item {
    visible: boolean;
    dimension: {
      width?: number;
      height?: number;
    };
    offset?: {
      top?: number;
      right?: number;
      bottom?: number;
      left?: number;
    }
  }

  export interface Items {
    adminBar: Item;
    pageBar: Item;
    builderBar: Item;
    sidebarLeft: Item & {
      props?: Sidebar.Props;
    };
    sidebarRight: Item & {
      props?: Sidebar.Props;
    };
  }

  export type Name = keyof Items;

  export namespace StyleProperty {
    export type Group = 'dimension' | 'offset';

    // TODO refactor this so no need to disable eslint
    // eslint-disable-next-line no-shadow
    export type Name = keyof Item['dimension'] | keyof Item['offset'];

    export type Value = Item['dimension'][keyof Item['dimension']];
  }
}
