export namespace Mode {
  export type Type = 'hover' | 'click' | 'grid';

  export interface Item {
    label: string;
    name: Type;
    iconName: string;
  }

  export type Items = Record<Type, Item>;

  export interface Interaction {
    optionsOnHover: Type[];
    optionsOnClick: Type[];
    options: Type[];
    highlightOnHover: Type[];
    highlight: Type[];
  }

  export type InteractionLocation = 'module' | 'parent';
}
