import { type MouseEvent } from 'react';

import { type View } from '../view';


export namespace PageBar {
  export interface Button {
    onClick: (event: MouseEvent<HTMLButtonElement>) => void;
    active: boolean;
  }

  export interface ResponsiveButton {
    onClick: (event: MouseEvent<HTMLButtonElement>) => void;
  }

  export interface Props {
    buttons: {
      addToLibrary: Button;
      portability: Button;
      pageSettings: Button;
    };
    responsive: {
      view: View.Type;
      buttons: {
        desktop: ResponsiveButton;
        tablet: ResponsiveButton;
        phone: ResponsiveButton;
      }
    };
    adminBarHeight: number;
    isSyncToServerInProgress: boolean;
    postStatus: string;
  }
}
