import { type Attr } from '../attr';


export namespace Attrs {
  export type ClipboardCategory = 'all' | 'content' | 'style';

  export type CategoryMapItem = Record<Attr.Name, Record<Attr.SubName, ''> | ''>;

  export type StructureMap = Record<Attr.Name, Record<Attr.SubName, ''>>;

  export type CategoryMap = Record<ClipboardCategory, CategoryMapItem>;

  export type GroupNameMap = Record<Attr.GroupName, Record<Attr.SubName, Attr.Name[]>>;

  export interface Maps {
    attrsStructureMap?: StructureMap;
    attrsCategoryMap?: CategoryMap;
    attrsGroupNameMap?: GroupNameMap;
  }

}
