import { type ReactNode } from 'react';

import { type Attrs } from '../attrs';


export namespace Component {

  /**
   * Component Attrs Maps.
   */
  export namespace AttrsMaps {
    export interface Base {
      component: ReactNode,

      // Generated, hence the optional
      id?: string;

      // @todo should be imported; do this later.
      attrsMaps?: Attrs.Maps;
    }

    export interface Module extends Base {
      attrsMapsType: 'module';
      name: string;      // eg. `divi/cta`
      callback?: (attrsMaps: Attrs.Maps) => void;
    }

    export interface Group extends Base {
      attrsMapsType: 'group';
      name: string;       // eg. `divi/sizing`
      moduleId: string;   // eg. `uuid`
      moduleName: string; // eg. `divi/cta`
      callback?: (attrsMaps: Attrs.Maps) => void;
    }

    export type Item = Module | Group;
  }
}
