export namespace Data {
  /* eslint-disable @typescript-eslint/naming-convention */

  export type Type = 'module' | 'row' | 'section' | 'layout' | 'page';

  export interface Taxonomy {
    id: number;
    name: string;
    slug: string;
    count?: number;
    layouts?: number[];
  }

  export interface Layout {
    categories: { [key: string]: Taxonomy };
    packs: any[];
    tags: { [key: string]: Taxonomy };
    items: LayoutItem[];
  }

  export interface LayoutItem {
    id: number;
    index: number;
    date: Date;
    type: string;
    width: string;
    row_layout: string;
    subtype: string;
    short_name: string;
    name: string;
    slug: string;
    url: string;
    thumbnail: string;
    thumbnail_small: string;
    screenshot: string;
    is_global: boolean;
    is_favorite: boolean;
    is_landing: boolean;
    description: string;
    isTrash: boolean;
    isReadOnly: boolean;
    categories: string[];
    category_ids: number[];
    tags: string[];
    tag_ids: number[];
    category_slug: string;
    tag_slug: string;
  }

  export interface Page {
    categories: { [key: string]: Taxonomy };
    packs: any[];
    items: PageItem[];
    options: PageOptions;
    sorted: PageSorted;
  }

  export interface PageItem {
    index: number;
    id: number;
    date: Date;
    status: string;
    icon: string;
    type: string;
    name: string;
    short_name: string;
    slug: string;
    url: string;
    thumbnail: string;
    thumbnail_small: string;
    categories: any[];
    category_ids: number[];
    is_global: boolean;
    is_landing: boolean;
    description: string;
    category_slug: string;
  }

  export interface PageOptions {
    content: PageOptionsContent;
    sidebar: PageOptionsSidebar;
    list: PageOptionsList;
  }

  export interface PageOptionsContent {
    title: string[];
  }

  export interface PageOptionsList {
    columns: PageOptionsColumns;
  }

  export interface PageOptionsColumns {
    status: string;
  }

  export interface PageOptionsSidebar {
    title: string;
    filterTitle: string;
  }

  export interface PageSorted {
    categories: number[];
    packs: any[];
  }

  export type Item = Layout | Page;

  export type Fetched = {
    [key in Type]?: Item;
  }

  export type FetchLog = {
    [key in Type]?: boolean;
  };
}
