export namespace Item {
  export interface Page {
    content: string;
  }

  export interface Layout extends Page {
    ID: number;
    title: string;
    is_global: string;
    layout_type: string;
    applicability: string;
    layouts_type: string;
    module_type: string;
    categories: string[];
    row_layout: string;
    unsynced_options: any[];
  }

  // TODO Refactor this
  // eslint-disable-next-line no-shadow
  export type Item = Page | Layout;

  export type Fetched = {
    [key: number]: Item;
  }

}
