export namespace BorderStylesPreview {

  /**
   * @typedef {('width'|'style'|'color')} Property
   */
  export type Property = 'width' | 'style' | 'color';

  /**
   * @typedef {('top'|'right'|'bottom'|'left')} Edge
   */
  export type Edge = 'top' | 'right' | 'bottom' | 'left';

  /**
   * @typedef {('all'|'top'|'right'|'bottom'|'left')} EdgeAll
   */
  export type EdgeAll = 'all' | Edge;

  /**
   * @typedef {object} Style
   */
  export type Style = Partial<Record<Property, string>>;

  /**
   * @typedef {object} Props
   * @property {string} name Name.
   * @property {object} value Value.
   * @property {object} [defaultValue] Default value.
   */
  export interface Props {
    name: string;
    value: Partial<Record<EdgeAll, Style>>;
    defaultValue?: Partial<Record<EdgeAll, Style>>;
  }
}
