import { type Scroll } from '../../module/element/decoration/scroll';


export namespace ScrollEffect {

  /**
   * @typedef {object} OnChangeCallbackParams
   * @property {object} inputValue Input value.
   * @property {string} inputValue.name Input name.
   * @property {object} inputValue.value Input value.
   */
  export interface OnChangeCallbackParams {
    inputValue: {
      name: string;
      value: Scroll.ItemValue;
    }
  }

  /**
   * @typedef {object} Props
   * @property {string} name Name.
   * @property {object} type Type.
   * @property {string} [className] Class name.
   * @property {Function} onChange On change callback.
   * @property {object} value Value.
   * @property {object} defaultValue Default value.
   */
  export interface Props {
    name: string;
    type: Scroll.ResolverName;
    className?: string;
    onChange: (params: OnChangeCallbackParams) => void;
    value: Scroll.ItemValue;
    defaultValue: Scroll.ItemValue;
  }
}
