export namespace TextArea {

/**
   * @typedef {object} OnChangeCallbackParams
   * @property {string} [inputValue] Input value.
   */
  export type OnChangeCallbackParams = {
    inputValue?: string;
  }

  /**
   * @typedef {object} Props
   * @property {string} [className] Class name.
   * @property {string} [name] Name.
   * @property {string} value Value.
   * @property {Function} onChange On change callback.
   * @property {('raw_content'|'custom_message')} [processing] Processing.
   * @property {boolean} ["readonly"] Read only.
   */
  export type Props = {
    value: string;
    onChange: (params: OnChangeCallbackParams) => void;
    className?: string;
    name?: string;
    processing?: 'raw_content' | 'custom_message';
    readonly?: boolean;
  };
}
