export namespace UploadGallery {

/**
   * @typedef {object} OnChangeCallbackParams params passed to onChange callback.
   * @property {MouseEvent} [event] Mouse event.
   * @property {string} [inputValue] Input value.
   */
  export type OnChangeCallbackParams = {
    event?: MouseEvent;
    inputValue?: string;
  }

  /**
   * @typedef {object} Props props passed to  component.
   * @property {string} [chooseText] Text to show on choose button.
   * @property {Function} onChange Callback function when input value changed.
   * @property {boolean} ["readonly"] Whether the input is readonly.
   * @property {string} value Input value.
   */
  export type Props = {
    chooseText?: string,
    onChange: (params: OnChangeCallbackParams) => void;
    readonly?: boolean,
    value: string;
  }
}
