export namespace Custom {
  export interface Font {
    /* eslint-disable-next-line @typescript-eslint/naming-convention */
    font_file: {
      ttf?: string;
      otf?: string;
    },
    /* eslint-disable-next-line @typescript-eslint/naming-convention */
    font_url: {
      ttf?: string;
      otf?: string;
    },
    styles: string;
    type: string;
  }

  export interface LoadedFont extends Font {
    fontType: 'customFonts';
  }

  export type Fonts = Record<string, Font>;
}
