import { type OnOff } from '../attr';
import { type FormatBreakpointStateAttr } from '../format-attr';


export namespace Icon {
  export type Data = {
    decodedUnicode: string;
    fontWeight: 400 | 900;
    name: string;
    searchTerms: string;
    styles: string[];
    unicode: string;
  }

  export namespace Font {
    export interface AttributeValue {
      unicode?: string,
      type?: string,
      weight?: '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
      color?: string;
      useSize?: OnOff;
      size?: string;
      show?: OnOff;
    }

    export type Attributes = FormatBreakpointStateAttr<AttributeValue>;
  }

  export type Type = {
    data: Data;
  }
}
