export namespace Shortcut {
  export interface Item {
    name: string;
    label: string;
    keys: string[];
    paramKeys?: string[];
    category: string;
    isEnabled?: () => boolean;
    action: () => void;
  }

  export type Items = Record<string, Item> | Record<string, Record<string, Item>>;

  export interface Map {
    0: string;
    1: number;
    2: string;
  }

  export type Group = 'length' | 'category' | false;
}
