export namespace Modal {
  export type ModalStates<TAttributes = Record<string, any>> = {
    name: string;
    owner: string;
    dimension: Modal.Dimension;
    offset: Offset;
    centered?: boolean;

    // Modal specific configuration.
    attributes?: TAttributes;

    tab: string;
    group: Record<string, string>;
    groupTab: Record<string, string>;
    expand: boolean;
    panelSize?: number;
    bodySiblingHeight?: number;
    isResizing?: boolean;
  }

  export type Dimension = {
    height: number;
    width: number;
  }

  export type Offset = {
    top: number;
    left: number;
  }

  export type Type = 'singleInstanceModal' | 'multiInstanceModal' | 'pageBarModal';
}
