export namespace Folder {

  /**
  * Folder category.
  *
  * @since ??
  */
  export type Category = 'module' | 'fullwidth-module';

  /**
  * Folder interface.
  *
  * @since ??
  */
  export type Definition = {
    name: string;
    title: string;
    path?: string;
    category?: Category;
    icon?: string;
  }

  export type Collections = Record<string, Definition>;
}
