/**
 * Classnames class' type.
 */
export type ClassnamesType = {
  values: Record<string, boolean>;

  /**
   * Classname constructor.
   */
  constructor: (initialClassnames: Record<string, boolean>) => void;

  /**
   * Add classname into classname instance.
   */
  add(classname: string | string[] | Record<string, boolean>, shouldBeAdded?: boolean): void;

  /**
   * Remove classname from classname instance.
   */
  remove(classname: string): void;

  /**
   * Get classname from classname instance.
   */
  value(): string;
}
