export interface DynamicContentOptionField {
  label: string;
  type: 'select' | 'select_post' | 'yes_no_button' | 'text';
  options?: Record<string, string>;
  // eslint-disable-next-line @typescript-eslint/naming-convention -- key coming from PHP.
  post_type?: string;
  // eslint-disable-next-line @typescript-eslint/naming-convention -- key coming from PHP.
  show_if?: Record<string, string>;
  default: string;
}

export interface DynamicContentOption {
  label: string;
  type: 'text' | 'url' | 'image';
  fields: Record<string, DynamicContentOptionField>;
  id: string;
}

export type DynamicContentOptions = Record<string, DynamicContentOption>;
