import { type FormatBreakpointStateAttr } from '../../../../format-attr';


export namespace Scroll {
  export type ResolverName = 'blur' | 'opacity' | 'rotate' | 'scale' | 'translateX' | 'translateY';

  export interface ItemValue {
    enable?: string;
    viewport: {
      bottom: string | number;
      end: string | number;
      start: string | number;
      top: string | number;
    },
    offset: {
      start: string | number;
      mid: string | number;
      end: string | number;
    },
  }

  export interface AttributeValue {
    verticalMotion?: ItemValue;
    horizontalMotion?: ItemValue;
    fade?: ItemValue;
    scaling?: ItemValue;
    rotating?: ItemValue;
    blur?: ItemValue;
    motionTriggerStart?: 'middle' | 'top' | 'bottom';
  }

  export type Attributes = FormatBreakpointStateAttr<AttributeValue>;
}
