import { type OnOff } from '../../../../attr';
import { type FormatBreakpointStateAttr } from '../../../../format-attr';
import { type Position } from '../position';
import { type Sizing } from '../sizing';


export namespace Sticky {
  export type Limit = 'none' | 'body' | 'section' | 'row' | 'column';

  export interface AttributeValue {
    position?: 'none' | 'top' | 'bottom' | 'topBottom';
    offset?: {
      top?: string;
      bottom?: string;
      surrounding?: OnOff;
    };
    limit?: {
      top?: Limit;
      bottom?: Limit;
    },
    transition?: OnOff;
  }

  export type Attributes = FormatBreakpointStateAttr<AttributeValue>;

  // Replace `UseStickyScriptDataContainerParams` with this.
  export interface ContainerHookParams {
    affectingAttrs: {
      position?: Position.Attributes,
      sizing?: Sizing.Attributes,
    };
    attr: Attributes,
    id: string;
    selector: string;
  }
}
