import { type Decoration } from '../decoration';


export namespace ScriptData {

  // In the UseStickyScriptDataContainerParams interface selector is mandatory.
  // But in ElementScriptData -> Sticky the selector is optional, hence handled separately.
  type ExcludedScriptProps = 'attr' | 'id';


  // TODO Replace `UseElementScriptDataParams` with this.
  export interface HookParams {

      // General Props.
      selector?: string;
      id?: string;
      attrs: Decoration.Attributes;

      // Script specific settings
      sticky?: Omit<Decoration.Sticky.ContainerHookParams, ExcludedScriptProps>;
  }

  // TODO Replace `ElementScriptDataProps` with this.
  export interface Props {

      // General Props.
      selector?: string;
      id?: string;
      attrs: Decoration.Attributes;

      // Script specific settings
      sticky?: Omit<Decoration.Sticky.ContainerHookParams, ExcludedScriptProps>;
  }

}
