import { type FormatBreakpointStateAttr } from '../../../../format-attr';


export namespace Image {
  export namespace InnerContent {

    // This interface specifies the structure of an object that represents the inner
    // content of an image. It has three optional properties: `src`, `alt`, and `title`, all of which are
    // of type `string`.
    export interface AttributeValue {
      src?: string;
      alt?: string;
      title?: string;
    }

    export type Attributes = FormatBreakpointStateAttr<AttributeValue>;
  }
}
