import { type UnsyncAttrs } from '../global-layout';


export interface InternalAttrs {

  // keep meta-related on first level for now to postpone selected meta-related issue to later phase
  // of module attribute refactor
  // The properties of `InternalAttrs` is now overlapping with `ModuleMetaAttrs` below because if we simply
  // rely in module.meta interface value, tons of tsc check returns error because CtaAttrs doesn't have
  // InternalAttrs in ModuleAttrs. Need to revisit whether we can keep InternalAttrs or actually move it to
  // module.meta. If we are moving it to module.meta, lots of path on global modules need to be modified.
  // TODO maybe move this to `module.meta`
  builderVersion?: string;
  modulePreset?: string;
  nonconvertible?: string;
  globalModule?: string;
  globalParent?: string;
  unsyncAttrs?: UnsyncAttrs;
}
