import { type Element } from '../element';
import { type Settings } from '../settings';


export namespace ElementType {
  export type GroupMap = {
    element: Record<string, Settings.AttributeGroup.Value>;
    module: Record<string, Omit<Settings.AttributeGroup.Item.Group, 'groupType'>>;
  };

  export interface GroupMapParams {
    elementName: string;
    elementLabel: string;

    // TODO Refactor this. This should've been `Module.Metadata.Values['category']` but it will cause
    // circular dependency.
    moduleCategory: string;
  }

  export type GroupMapFunctionMap = Partial<Record<
    Element.Properties['elementType'], (params: GroupMapParams) => GroupMap
  >>
}
