import { type Field } from '../field';
import { type Group } from '../group';


export namespace FieldOrGroup {

  // Field Component
  export type FieldItem = Partial<Field.BaseProps & Field.ContainerProps>;

  // Option Group Component.
  export type GroupItem = Partial<Field.BaseProps & {
    component: Group.Library.Component;
  }>;

  /**
   * Field or Group component item.
   */
  export type Item = FieldItem | GroupItem;

  /**
   * Field or group component item that is used by `<ModuleFields />`'s `props.fields`.
   */
  export type Items = Record<string, Item>;
}
