import { type Field } from '../field';

/**
 * Props of module options group field component.
 *
 * @since ??
 *
 * @typedef Group.FieldProps
 * @property {boolean} [container] Whether the field is container or not.
 * @property {string} [groupName] Group name of the field.
 * @property {string} [id] Id of the field.
 * @property {number} [priority] Priority of the field.
 * @property {boolean} [render] Whether the field is render or not.
 * @property {Partial<Field.ContainerProps>} [TModuleFieldContainerProps] Module field container props.
 */
export type GroupFieldProps<TModuleFieldContainerProps = Partial<Field.ContainerProps>> = {
  container?: boolean;
  groupName?: string;
  id?: string;
  priority?: number;
  render?: boolean;
} & TModuleFieldContainerProps;
