import { type Field } from '../field';
import { type GroupFieldProps } from './field';
import { Library as LibraryNamespace } from './library';
import { type GroupProps } from './props';

// If a namespace has sub-namespace inside of it, type/interface should not be declared directly
// inside of it. It'll cause circular dependency issue when the type/interface is used inside
// of its sub-namespace.

export namespace Group {

  /**
   * Module Settings' group component base props.
   */
  export type Props = GroupProps;

  /**
   * Module Settings' field component's base props.
   */
  export type FieldProps<TModuleFieldContainerProps = Partial<Field.ContainerProps>>
    = GroupFieldProps<TModuleFieldContainerProps>;

  /* eslint-disable @typescript-eslint/no-unused-vars */
  export import Library = LibraryNamespace;
  /* eslint-enable @typescript-eslint/no-unused-vars */
}
