/**
 * Map of group component props, WITHOUT divi/composite group. The exception is done
 * so that `divi/composite`'s `GroupProps` can use this map without having
 * circular dependency issue.
 */
export interface GroupLibraryMapWithoutComposite {
  'divi/admin-label': {
    props: AdminLabel.GroupProps;
  };
  'divi/animation': {
    props: Animation.GroupProps;
  };
  'divi/attributes-rel': {
    props: AttributesRel.GroupProps;
  };
  'divi/background': {
    props: Background.GroupProps;
  };
  'divi/border': {
    props: Border.GroupProps;
  };
  'divi/box-shadow': {
    props: BoxShadow.GroupProps;
  };
  'divi/button': {
    props: Button.GroupProps;
  };
  'divi/button-icon': {
    props?: ButtonIcon.GroupProps;
  };
  'divi/button-link': {
    props?: ButtonLink.GroupProps;
  };
  'divi/conditions': {
    props?: Conditions.GroupProps;
  };
  'divi/css': {
    props?: Css.GroupProps
  };
  'divi/disabled-on': {
    props?: DisabledOn.GroupProps;
  }
  'divi/dividers': {
    props?: Dividers.GroupProps;
  };
  'divi/email-service': {
    props?: EmailService.GroupProps;
  };
  'divi/filters': {
    props?: Filters.GroupProps;
  };
  'divi/font': {
    props?: Font.GroupProps;
  };
  'divi/font-body': {
    props?: FontBody.GroupProps;
  };
  'divi/font-header': {
    props?: FontHeader.GroupProps;
  };
  'divi/form-field': {
    props?: FormField.GroupProps;
  };
  'divi/gutter': {
    props?: Gutter.GroupProps;
  };
  'divi/icon': {
    props?: Icon.GroupProps;
  };
  'divi/id-classes': {
    props?: IdClasses.GroupProps;
  };
  'divi/link': {
    props?: Link.GroupProps;
  };
  'divi/overflow': {
    props?: Overflow.GroupProps;
  };
  'divi/position': {
    props?: Position.GroupProps;
  };
  'divi/position-settings': {
    props?: PositionSettings.GroupProps;
  };
  'divi/scroll': {
    props?: Scroll.GroupProps;
  };
  'divi/scroll-settings': {
    props?: ScrollSettings.GroupProps;
  };
  'divi/sizing': {
    props?: Sizing.GroupProps;
  };
  'divi/spacing': {
    props?: Spacing.GroupProps;
  };
  'divi/spam-protection': {
    props?: SpamProtection.GroupProps;
  };
  'divi/sticky': {
    props?: Sticky.GroupProps;
  }
  'divi/text': {
    props?: Text.GroupProps;
  }
  'divi/text-shadow': {
    props?: TextShadow.GroupProps;
  };
  'divi/transform': {
    props?: Transform.GroupProps;
  };
  'divi/transition': {
    props?: Transition.GroupProps;
  };
  'divi/visibility-settings': {
    props?: VisibilitySettings.GroupProps;
  };
  'divi/z-index': {
    props?: ZIndex.GroupProps;
  };
}
