import { type ReactNode } from 'react';

/**
 * Props of module settings' group component props.
 *
 * @since ??
 *
 * @typedef Group.Props
 * @property {boolean} [grouped] Whether the field is grouped or not.
 * @property {string} [groupLabel] Group label.
 * @property {string} [fieldLabel] Field label.
 * @property {string} [groupTabLabel] Group tab label.
 * @property {boolean} [visible] Whether the field is visible or not.
 * @property {string} [icon] Icon of the field.
 * @property {string} [attrName] Attribute name of the field.
 * @property {string} [groupTab] Group tab of the field.
 * @property {ReactNode} [children] Children of the field.
 */
export interface GroupProps {
  grouped?: boolean;
  groupLabel?: string;
  fieldLabel?: string;
  groupTabLabel?: string;
  visible?: boolean;
  icon?: string;
  attrName?: string;
  groupTab?: string;
  children?: ReactNode,
}
