import {
  type ModuleAttrs,
  type ModuleAttrsByName,
} from '../library';


export interface ModuleFlatObjectNamed<TName extends string> {
  children: string[];
  id: string;
  name: TName;
  parent?: string;
  isGlobal?: boolean;
  props: {
    attrs?: ModuleAttrsByName<TName>;
    innerHTML?: string;
  },
}

/**
 * Module's flat object interface which its state is managed at divi/edit-post store.
 *
 * @since ??
 */
export interface ModuleFlatObject<TAttrs = ModuleAttrs> {
  children: string[];
  id: string;
  name: string;
  parent?: string;
  props: {
    attrs?: TAttrs;
    innerHTML?: string;
  },
}

