import { type MouseEvent } from 'react';


export namespace Option {
  export interface Item {
    name: string;
    label: string;
    onClick: (event: MouseEvent) => void;
    children?: null | Items;
    topSeparator?: boolean;
    bottomSeparator?: boolean;
    shortcut?: string[];
  }

  // TODO to be renamed
  export type Items = Item[];
}
