import { type Field } from '../field';


export namespace GroupField {
  export type Item = Record<string, Field.Items>;

  export type SubToggle = string | { name?: string; icon?: string };

  export type SubToggles = Record<string, SubToggle>;

  export interface Setting {
    bb_icons_support?: boolean;
    priority?: number;
    sub_toggles?: SubToggles;
    tabbed_subtoggles?: boolean;
    title?: string;
    always_open?: boolean;
  }
}
