/**
 * Get currently hovered sidebar modal's docking area based on given (currently dragged) modal offset value.
 *
 * `getDockingAreas()` creates map of offsets + dimension of each sidebar modal docking areas
 * based on current state. This functions takes modal's offsets value and see which docking area that
 * are eligible for current modal offset position. If no eligible docking area found, returns `false`.
 *
 * @since ??
 *
 * @group Selector Functions
 *
 * @remarks Data Store Selector Function
 *
 * @example
 * ```ts
 * window.divi.data.select('divi/app-ui').getHoveredDockingArea({
 *   modalOffset: {
 *     top:  50,
 *     left: 50,
 *   },
 *   dockingAreas: [
 *     {
 *       id:  'sidebarLeft',
 *       top: 60,
 *       left: 60,
 *       width: 300,
 *       height: 800,
 *       type: 'sidebar',
 *     }
 *   ]
 * });
 * ```
 */
export declare const getHoveredDockingArea: (state: AppUi.Store.ImmutableState, modalOffset: AppUi.Sidebar.Offset, dockingAreas?: AppUi.Docking.Areas) => false | AppUi.Docking.Area;
