import { type ModuleAttr } from '@divi/types';
/**
 * Extract Field Attr Value.
 *
 * Extract field's related attribute from module attribute in ResponsiveAttr interface.
 * This is needed because field value can be located inside subname and attribute name.
 * Can be nested.
 *
 * @since ??
 *
 * @param {object} attrValue Module attributE.
 * @param {string} subName Field attribute subnamE.
 *
 * @returns {object} Extracted field attribute value.
 *
 * @example
 * ```js
 * const attrValue = {
 *  desktop: {
 *   value: {
 *    subName: 'value',
 *    subName2: 'value 2',
 *   },
 *  },
 * };
 * const subName = 'subName';
 * const extractedAttrValue = extractFieldAttrValue(attrValue);
 * // {
 * //  desktop: {
 * //   value: {
 * //    subName: 'value',
 * //    subName2: 'value 2',
 * //   },
 * //  },
 * // }
 * ```
 *
 * @example
 * ```js
 * const attrValue = {
 *  desktop: {
 *   value: {
 *    subName: 'value',
 *    subName2: 'value 2',
 *   },
 *  },
 * };
 * const subName = 'subName';
 * const extractedAttrValue = extractFieldAttrValue(attrValue, subName);
 * // {
 * //  desktop: {
 * //   value: 'value',
 * //  },
 * // }
 * ```
 */
export declare const extractFieldAttrValue: (attrValue: ModuleAttr, subName?: string) => ModuleAttr;
