import { type GetAttrValueParams } from './types';
/**
 * Get Attr Value.
 *
 * Get attr value and/or its inherited value from larger breakpoint / default state.
 * Getter and inheritance model can be changed based on `mode` parameter:
 * 1. `get`                  : Get attr value of given breakpoint + state.
 * 2. `getAndInheritAll`     : Get attr value combined by all possible inherited attr value on all larger breakpoints.
 * 3. `getAndInheritClosest` : Get attr value combined by inherited attr value from closest available breakpoint.
 * 4. `getOrInheritAll`      : Get attr value or inherited attr value from all larger breakpoints.
 * 5. `getOrInheritClosest`  : Get attr value or inherited attr value from closest available breakpoint.
 * 6. `inheritAll`           : Get inherited attr value from all larger breakpoints.
 * 7. `inheritClosest`       : Get inherited attr value from all closest available breakpoint.
 * See below for inherited attribute fallback flow:
 * ----------------------------------.
 * |       | value | hover | sticky |
 * ----------------------------------.
 * Desktop |   *   |  <--  |  <--   |
 * ----------------------------------.
 * Tablet  |   ^   |  <--  |  <--   |
 * ----------------------------------.
 * Phone   |   ^   |  <--  |  <--   |
 * ----------------------------------.
 *
 * @since ??
 *
 * @param {object} [param] Function parameters.
 * @param {object} [param.attr] Object containing attribute values for different breakpoints and states.
 * @param {string} [param.breakpoint] Current breakpoint.
 * @param {string} [param.state] Current state.
 * @param {string} [param.mode='getOrInheritAll'] Getter and inheritance mode.
 * @param {object} [param.defaultValue] Default value if subname not found.
 *
 * @returns {string} Value for given breakpoint and state.
 *
 * @example
 * ```js
 * const attr = {
 *   desktop: {
 *     value: 'desktop',
 *     hover: 'desktop-hover',
 *     sticky: 'desktop-sticky',
 *   },
 *   tablet: {
 *     value: 'tablet',
 *     hover: 'tablet-hover',
 *     sticky: 'tablet-sticky',
 *   },
 *   phone: {
 *     value: 'phone',
 *     hover: 'phone-hover',
 *     sticky: 'phone-sticky',
 *   },
 * };
 *
 * getAttrValue({
 *  attr,
 *  breakpoint: 'desktop',
 *  state: 'value',
 *  mode: 'getAndInheritAll',
 * });
 *
 * // Returns: 'desktop'
 */
export declare const getAttrValue: <TAttrValue>({ attr, breakpoint, state, defaultValue, mode, }: GetAttrValueParams<TAttrValue>) => TAttrValue;
